import React, { useState, useEffect } from 'react';
import axios from 'axios';
import FilmsList from './FilmsList';
import './ContainerFilms.css';

const ContainerFilms = () => {
    const [films, setFilms] = useState({ films: [] });
    const [currentPage, setCurrentPage] = useState(1);
    const filmsPerPage = 30;
    const [totalPages, setTotalPages] = useState(0);

    const getMovies = async () => {
        const {
            data: {
                data: { movies, movie_count },
            },
        } = await axios.get('https://yts.mx/api/v2/list_movies.json', {
            params: {
                page: currentPage,
                limit: filmsPerPage,
            },
        });
        setFilms({ films: movies });
        const totalPages = Math.ceil(movie_count / filmsPerPage);
        setTotalPages(totalPages);
    };

    const goToPage = (page) => {
        setCurrentPage(page);
    };

    const goToPreviousPage = () => {
        setCurrentPage((prevPage) => prevPage - 1);
    };

    const goToNextPage = () => {
        setCurrentPage((prevPage) => prevPage + 1);
    };

    useEffect(() => {
        getMovies();
    }, [currentPage]); // Запускаем запрос на сервер при изменении currentPage

    // Генерация кнопок с номерами страниц
    const maxPageButtons = 10; // Максимальное количество отображаемых кнопок навигации
    const startPage = Math.max(1, currentPage - Math.floor(maxPageButtons / 2));
    const endPage = Math.min(startPage + maxPageButtons - 1, totalPages);
    const pageNumbers = Array.from({ length: endPage - startPage + 1 }, (_, index) => startPage + index);

    return (
        <div className="wrapper">
            {films.films.map((e) => (
                <FilmsList
                    key={e.id}
                    poster={e.medium_cover_image}
                    genre={e.genres}
                    title={e.title}
                    year={e.year}
                    rate={e.rating}
                />
            ))}
            {totalPages > 1 && (
                <div className="pagination">
                    <button onClick={goToPreviousPage} disabled={currentPage === 1}>
                        ❮
                    </button>
                    {pageNumbers.map((pageNumber) => (
                        <button
                            key={pageNumber}
                            onClick={() => goToPage(pageNumber)}
                            className={currentPage === pageNumber ? 'active' : ''}
                        >
                            {pageNumber}
                        </button>
                    ))}
                    <button onClick={goToNextPage} disabled={currentPage === totalPages}>
                        ❯
                    </button>
                </div>
            )}
        </div>
    );
};

export default ContainerFilms;
