import React from "react";

class StreamTextClass extends React.Component {
    constructor(props) {
        super(props);
        this.state = {
            value: ''
        };
    }

    inputState = (e) => {
        this.setState({ value: e.target.value });
    }

    render() {
        return (
            <div>
                <input placeholder="Class component" type="text" onChange={this.inputState} />
                <h1>{this.state.value}</h1>
            </div>
        );
    }
}

export default StreamTextClass;
