import React, { useState, useEffect } from 'react';
import axios from 'axios';
import FilmsList from './FilmsList';
import './ContainerFilms.css';

const ContainerFilms = () => {
  const [films, setFilms] = useState({ films: [] });
  const [currentPage, setCurrentPage] = useState(1);
  const filmsPerPage = 32;
  const [totalPages, setTotalPages] = useState(0);
  const [sortBy, setSortBy] = useState('');

  const getMovies = async () => {
    const {
      data: {
        data: { movies, movie_count },
      },
    } = await axios.get('https://yts.mx/api/v2/list_movies.json', {
      params: {
        page: currentPage,
        limit: filmsPerPage,
        sort_by: sortBy,
      },
    });
    setFilms({ films: movies });
    const totalPages = Math.ceil(movie_count / filmsPerPage);
    setTotalPages(totalPages);
  };

  const goToPreviousPage = () => {
    setCurrentPage((prevPage) => prevPage - 1);
  };

  const goToNextPage = () => {
    setCurrentPage((prevPage) => prevPage + 1);
  };

  useEffect(() => {
    getMovies();
  }, [currentPage, sortBy]); // Запускаем запрос на сервер при изменении currentPage или sortBy

  const handleSortBy = (sortType) => {
    setSortBy(sortType);
  };

  return (
    <div className="wrapper">
      <div className="sort-menu">
        <span>Sort By:</span>
        <select value={sortBy} onChange={(e) => handleSortBy(e.target.value)}>
          <option value="">Default</option>
          <option value="rating">Rating (Ascending)</option>
          <option value="rating_desc">Rating (Descending)</option>
          <option value="title">Title (Ascending)</option>
          <option value="title_desc">Title (Descending)</option>
          <option value="year">Year (Ascending)</option>
          <option value="year_desc">Year (Descending)</option>
        </select>
      </div>
      {films.films.map((e) => (
        <FilmsList
          key={e.id}
          poster={e.medium_cover_image}
          genre={e.genres}
          title={e.title}
          year={e.year}
          rate={e.rating}
        />
      ))}
      {totalPages > 1 && (
        <div className="pagination">
          <button onClick={goToPreviousPage} disabled={currentPage === 1}>
            ❮ Пред. Страница
          </button>
          <h2>{currentPage}</h2>
          <button onClick={goToNextPage} disabled={currentPage === totalPages}>
            След. страница ❯
          </button>
        </div>
      )}
    </div>
  );
};

export default ContainerFilms;
