import React, { useState, useEffect } from 'react';
import axios from 'axios';
import FilmPrev from './FilmPrev';
import './ContainerFilms.css';

const ContainerFilms = ({ sortValue }) => {
  const [films, setFilms] = useState([]);
  const [currentPage, setCurrentPage] = useState(1);
  const filmsPerPage = 32;
  const [totalPages, setTotalPages] = useState(0);

  useEffect(() => {
    getMovies();
  }, [currentPage]);

  useEffect(() => {
    sortFilms(sortValue);
  }, [sortValue]);

  const sortFilms = (sortValue) => {
    const tmpFilms = [...films];
    if (sortValue === 'title') {
      tmpFilms.sort((a, b) => {
        if (a.title < b.title) return -1;
        if (a.title > b.title) return 1;
        return 0;
      });
    }
    setFilms(tmpFilms);
  };

  const getMovies = async () => {
    const {
      data: {
        data: { movies, movie_count },
      },
    } = await axios.get('https://yts.mx/api/v2/list_movies.json', {
      params: {
        page: currentPage,
        limit: filmsPerPage,
      },
    });
    setFilms(movies);
    const totalPages = Math.ceil(movie_count / filmsPerPage);
    setTotalPages(totalPages);
  };

  const goToPreviousPage = () => {
    setCurrentPage((prevPage) => prevPage - 1);
  };

  const goToNextPage = () => {
    setCurrentPage((prevPage) => prevPage + 1);
  };

  return (
    <div className="wrapper">
      {films.map((e) => (
        <FilmPrev
          key={e.id}
          poster={e.medium_cover_image}
          genre={e.genres}
          title={e.title}
          year={e.year}
          rate={e.rating}
        />
      ))}
      {totalPages > 1 && (
        <div className="pagination">
          <button onClick={goToPreviousPage} disabled={currentPage === 1}>
            ❮ Пред. Страница
          </button>
          <h2>{currentPage}</h2>
          <button onClick={goToNextPage} disabled={currentPage === totalPages}>
            След. страница ❯
          </button>
        </div>
      )}
    </div>
  );
};

export default ContainerFilms;
