import axios from 'axios';
import React from 'react';
import FilmsList from './Components/FilmsList';
import './styles/App.css'

class App extends React.Component {

  state = {
    isLoading: true,
    films: []
  }

  getMovies = async () => {
    const {
      data: {
        data: {
          movies } } } = await axios.get('https://yts.mx/api/v2/list_movies.json')
    this.setState({ films: movies, isLoading: false })
  }

  componentDidMount() {
    this.getMovies()
  }
  render() {
    const { isLoading, films } = this.state
    return (
      <div>
        {isLoading ? "Загрузка" : films.map(e => <FilmsList poster={e.medium_cover_image} title={e.title} year={e.year}   />)}
      </div>
    )
  }
}
export default App;
