import React, { useState, useEffect } from 'react';
import axios from 'axios';
import FilmPrev from './FilmPrev';
import './ContainerFilms.css';

const ContainerFilms = ({ sortValue }) => {
    const [films, setFilms] = useState({ films: [] });
    const [currentPage, setCurrentPage] = useState(1);
    const filmsPerPage = 32;
    const [totalPages, setTotalPages] = useState(0);

    const sortFilms = (sortValue) => {
        let sortedFilms = [...films.films];

        switch (sortValue) {
            case 'title_asc':
                sortedFilms.sort((a, b) => a.title.localeCompare(b.title));
                break;
            case 'title_desc':
                sortedFilms.sort((a, b) => b.title.localeCompare(a.title));
                break;
            case 'year_asc':
                sortedFilms.sort((a, b) => a.year - b.year);
                break;
            case 'year_desc':
                sortedFilms.sort((a, b) => b.year - a.year);
                break;
            case 'rating_asc':
                sortedFilms.sort((a, b) => a.rating - b.rating);
                break;
            case 'rating_desc':
                sortedFilms.sort((a, b) => b.rating - a.rating);
                break;
            default:
                break;
        }

        setFilms({ films: sortedFilms });
    };

    const getMovies = async () => {
        const {
            data: {
                data: { movies, movie_count },
            },
        } = await axios.get('https://yts.mx/api/v2/list_movies.json', {
            params: {
                page: currentPage,
                limit: filmsPerPage,
            },
        });
        setFilms({ films: movies });
        const totalPages = Math.ceil(movie_count / filmsPerPage);
        setTotalPages(totalPages);
    };

    const goToPreviousPage = () => {
        setCurrentPage((prevPage) => prevPage - 1);
    };

    const goToNextPage = () => {
        setCurrentPage((prevPage) => prevPage + 1);
    };

    useEffect(() => {
        sortFilms(sortValue);
    }, [sortValue]);

    useEffect(() => {
        getMovies();
    }, [currentPage]);

    return (
        <div className="wrapper">
            {films.films.map((e) => (
                <FilmPrev
                    key={e.id}
                    poster={e.medium_cover_image}
                    genre={e.genres}
                    title={e.title}
                    year={e.year}
                    rate={e.rating}
                />
            ))}
            {totalPages > 1 && (
                <div className="pagination">
                    <button
                        onClick={goToPreviousPage}
                        disabled={currentPage === 1}
                    >
                        ❮ Пред. Страница
                    </button>
                    <h2>{currentPage}</h2>
                    <button
                        onClick={goToNextPage}
                        disabled={currentPage === totalPages}
                    >
                        След. страница ❯
                    </button>
                </div>
            )}
        </div>
    );
};

export default ContainerFilms;
