import React from 'react';
import './Header.css';

const Header = () => {
    return (
        <header className="header">
            <div className="logo-sort-block">
                <h1>Films-Catalogy</h1>
                .sor
                <label htmlFor="sortSelect">Сортировать по</label>
                <select id='sortSelect' className='sort-select'>
                    <option value="1">По-умолчанию</option>
                    <option value="2">Год</option>
                    <option value="3">Рейтинг</option>
                </select>
            </div>
            <nav className="nav">
                <div className="search-block">
                    <input className="search-input" type="text" placeholder="Название фильма" />
                    <button className="search-btn">Поиск</button>
                </div>
                <ul className="nav-menu">
                    <li className="nav-item"><a href="">Новинки</a></li>
                    <li className="nav-item"><a href="">Жанры</a></li>
                    <li className="nav-item"><a href="">Случайный</a></li>
                </ul>
            </nav>
        </header>
    );
};

export default Header;
