import React from "react";
import { useState } from "react";

const AddFilm = ({ callback }) => {
    const [value, setValue] = useState({
        nameFilm: '',
        year: '',
        genreOne: '',
        genreTwo: ''
    })

    const getValueFilm = () => {
        callback(value);
    };
    return (
        <div>
            <input onChange={e => setValue(nameFilm = e.target.value)} placeholder="Название" type="text" />
            <input onChange={e => setValue(year = e.target.value)} placeholder="Год" type="text" />
            <input onChange={e => setValue(genreOne = e.target.value)} placeholder="Жанр1" type="text" />
            <input onChange={e => setValue(genreTwo = e.target.value)} placeholder="Жанр2" type="text" />
            <button onClick={getValueFilm(value)}>Добавить</button>
        </div>
    )
}

export default AddFilm;
