import React from "react";
import { useState } from "react";
import AddFilm from "../AddFilms";
import PrevFilm from "../PrevFilm/PrevFilm";
import classes from './FilmContainer.module.css'

const FilmContainer = () => {
    const [films, setFilms] = useState([
        {
            name: 'Перевозчик',
            year: '2021',
            genreOne: 'Боевик',
            genreTwo: 'Триллер'
        },
        {
            name: 'Форсаж',
            year: '2021',
            genreOne: 'Боевик',
            genreTwo: 'Триллер'
        },
        {
            name: 'Гнев человеческий',
            year: '2021',
            genreOne: 'Боевик',
            genreTwo: 'Триллер'
        }
    ])
    const handleFilmSubmit = (film) => {
        setFilms(prevFilms => [...prevFilms, film]);
      };
    return (
        <div className={classes.filmContainer}><div className={classes.filmContainer}>
            
            <AddFilm callback={handleFilmSubmit} />
            {films.map(e => (
                <PrevFilm
                    key={e.name}
                    name={e.name}
                    year={e.year}
                    genreOne={e.genreOne}
                    genreTwo={e.genreTwo}
                />
            ))}
        </div>
        </div>
    )
}

export default FilmContainer;
