import React, { useState } from 'react';
import axios from 'axios';
import FilmsList from './FilmsList';
import './ContainerFilms.css';

const ContainerFilms = () => {
    const [films, setFilms] = useState({ films: [] });

    const [currentPage, setCurrentPage] = useState(1);
    const filmsPerPage = 10;

    const getMovies = async () => {
        const {
            data: {
                data: { movies },
            },
        } = await axios.get('https://yts.mx/api/v2/list_movies.json', {
            params: {
                page: currentPage, // Указываем текущую страницу
                limit: filmsPerPage, // Указываем количество фильмов на странице
            },
        });
        setFilms({ films: movies });
    };
    const goToPreviousPage = () => {
        setCurrentPage((prevPage) => prevPage - 1);
      };
      
      const goToNextPage = () => {
        setCurrentPage((prevPage) => prevPage + 1);
      };
    useState(() => {
        getMovies();
    }, []);

    return (
        <div className="wrapper">
          {films.films.map((e) => (
            <FilmsList
              key={e.id}
              poster={e.medium_cover_image}
              genre={e.genres}
              title={e.title}
              year={e.year}
              rate={e.rating}
            />
          ))}
          <div className="pagination">
            <button onClick={goToPreviousPage} disabled={currentPage === 1}>
            ❮
            </button>
            di
            <button onClick={goToNextPage}>❯</button>
          </div>
        </div>
      );
};

export default ContainerFilms;
