import React from "react";
import { useState } from "react";

class StreamText extends React.Component {
    constructor(props) {
        super(props);
        this.state = {
            value: ''
        };
    }

    inputState = (e) => {
        setValue(e.target.value)
    }
    render({ value, setValue }) {
        return (
            <div>
                <input type="text" onInput={e => inputState(e)} />
                <h1>{value}</h1>
            </div>
        )
    }
}

export default StreamText
