import React from "react";
import { useState } from "react";

const StreamText = ({ value, setValue }) => {
    function inputState (e){
        setValue(e.target.value)
    }
    return (
        <div>
            <input placeholder="" type="text" onInput={e => inputState(e)} />
            <h1>{value}</h1>
        </div>
    )
}

export default StreamText
