import React from 'react';
import axios from 'axios';
import { useState } from 'react';
import FilmsList from './FilmsList';
import './ContainerFilms.css'

const ContainerFilms = () => {

    const [films, setFilms] = useState(({
        films: []
    }))
    const getMovies = async () => {
        const {
            data: {
                data: {
                    movies } } } = await axios.get('https://yts.mx/api/v2/list_movies.json')
        setFilms({ films: movies })
    }
    return (
        <div className="wrapper">
            {films.map(e => <FilmsList poster={e.medium_cover_image} title={e.title} year={e.year} genre={e.genres[0]} />)}

        </div>
    )
}

export default ContainerFilms
