import React from "react";

class Count extends React.Component {
    constructor(props) {
        super(props);
        this.state = {
            count: 0
        };
    }
    render() {
        return (
            <div>
                <h1>Вы кликнули: {this.state.count} раз</h1>
                <button onClick={() => this.setState({ count: this.count + 1 })}>Click</button>
                <button onClick={() => this.setState({ count: prevState.count = 0 })}>Reset</button>

            </div>
        )
    }
}

export default Count;
